import React, { useState, useEffect } from 'react';
import LocationPicker from './LocationPicker'; // Location Picker component
import AQIDisplay from './AQIDisplay';
import '../styles/main.css'; // Import the CSS for custom styling

const ForecastAQI = () => {
  const [data, setData] = useState(null);
  const [location, setLocation] = useState(null);

  useEffect(() => {
    if (location) {
      fetch(`http://localhost:5000/api/forecast?lat=${location.lat}&lon=${location.lon}`)
        .then(res => res.json())
        .then(setData)
        .catch(console.error);
    }
  }, [location]);

  return (
    <div>
      <h2>AQI Forecast</h2>
      <LocationPicker onLocationSelect={setLocation} />
      {data && (
        <>
          <div className="chart-container">
            {/* Chart rendering */}
          </div>
          <div className="forecast-list">
            {data.map((entry, index) => (
              <AQIDisplay key={index} data={entry} />
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default ForecastAQI;
