import React, { useState } from 'react';
import CurrentAQI from './components/CurrentAQI';
import ForecastAQI from './components/ForecastAQI';
import HistoricalAQI from './components/HistoricalAQI';

const App = () => {
  const [activeTab, setActiveTab] = useState('current');

  const getTabButtonClass = (tabName) => {
    const baseClass = "flex-1 px-6 py-3 text-sm font-medium rounded-lg transition-colors";
    return activeTab === tabName
      ? `${baseClass} bg-blue-600 text-white`
      : `${baseClass} hover:bg-blue-50 text-gray-600`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-blue-100 p-4 sm:p-8">
      <div className="max-w-6xl mx-auto bg-white/80 backdrop-blur-sm rounded-xl shadow-lg">
        <div className="text-center p-6">
          <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
            Delhi AQI Monitor
          </h1>
        </div>

        <div className="p-4">
          {/* Tab Buttons */}
          <div className="flex space-x-2 mb-8 bg-gray-100 p-2 rounded-lg">
            <button
              onClick={() => setActiveTab('current')}
              className={getTabButtonClass('current')}
            >
              Current AQI
            </button>
            <button
              onClick={() => setActiveTab('forecast')}
              className={getTabButtonClass('forecast')}
            >
              Forecast AQI
            </button>
            <button
              onClick={() => setActiveTab('historical')}
              className={getTabButtonClass('historical')}
            >
              Historical AQI
            </button>
          </div>

          {/* Tab Content */}
          <div className="mt-6">
            {activeTab === 'current' && <CurrentAQI />}
            {activeTab === 'forecast' && <ForecastAQI />}
            {activeTab === 'historical' && <HistoricalAQI />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
