const HistoricalAQI = () => {
  const [data, setData] = useState(null);
  const [location, setLocation] = useState(null);
  const [date, setDate] = useState('');

  const fetchData = () => {
    if (location && date) {
      const start = Math.floor(new Date(date).getTime() / 1000);
      const end = start + (24 * 60 * 60);
      
      fetch(`http://localhost:5000/api/historical?lat=${location.lat}&lon=${location.lon}&start=${start}&end=${end}`)
        .then(res => res.json())
        .then(setData)
        .catch(console.error);
    }
  };

  return (
    <div>
      <h2>Historical AQI</h2>
      <div className="controls">
        <LocationPicker onLocationSelect={setLocation} />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="date-picker" // Applying custom styling for the date picker
        />
        <button onClick={fetchData} className="fetch-btn">Get Data</button>
      </div>
      {data && (
        <>
          <div className="chart-container">
            {/* Chart rendering */}
          </div>
          <div className="historical-list">
            {data.map((entry, index) => (
              <AQIDisplay key={index} data={entry} />
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default HistoricalAQI;
