import React from 'react';
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";

const AQIDisplay = ({ data }) => {
  const getColorClass = (aqi) => {
    if (aqi <= 50) return 'bg-green-100 text-green-800';
    if (aqi <= 100) return 'bg-yellow-100 text-yellow-800';
    if (aqi <= 150) return 'bg-orange-100 text-orange-800';
    if (aqi <= 200) return 'bg-red-100 text-red-800';
    if (aqi <= 300) return 'bg-purple-100 text-purple-800';
    return 'bg-gray-100 text-gray-800';
  };

  return (
    <Card className="overflow-hidden">
      <CardContent className="p-6">
        <div className="flex flex-col md:flex-row justify-between items-center mb-6">
          <div className="text-center md:text-left mb-4 md:mb-0">
            <h2 className="text-4xl font-bold text-gray-900">
              {data.Final_AQI}
            </h2>
            <Badge className={`mt-2 ${getColorClass(data.Final_AQI)}`}>
              {data.AQI_Category}
            </Badge>
          </div>
          {data.datetime && (
            <div className="text-sm text-gray-500">
              {new Date(data.datetime).toLocaleString()}
            </div>
          )}
        </div>

        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {[
            { label: 'PM2.5', value: data.PM2_5_AQI },
            { label: 'PM10', value: data.PM10_AQI },
            { label: 'NO2', value: data.NO2_AQI },
            { label: 'O3', value: data.O3_AQI },
            { label: 'CO', value: data.CO_AQI },
            { label: 'SO2', value: data.SO2_AQI },
          ].map((pollutant) => (
            <div 
              key={pollutant.label}
              className="bg-white rounded-lg p-4 shadow-sm hover:shadow-md transition-shadow"
            >
              <div className="text-sm text-gray-500">{pollutant.label}</div>
              <div className="text-xl font-semibold mt-1">{pollutant.value}</div>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
  );
};

export default AQIDisplay;
