import React, { useState, useEffect } from 'react';
import LocationPicker from './LocationPicker';
import { FaCloud } from 'react-icons/fa';

const CurrentAQI = () => {
  const [data, setData] = useState(null);
  const [location, setLocation] = useState(null);

  useEffect(() => {
    if (location) {
      fetch(`http://localhost:5000/api/current?lat=${location.lat}&lon=${location.lon}`)
        .then(res => res.json())
        .then(setData)
        .catch(console.error);
    }
  }, [location]);

  return (
    <div>
      <h2>
      Current AQI</h2>
      <LocationPicker onLocationSelect={setLocation} />
      {data && (
        <div className="aqi-display">
          <h3>AQI: {data.Final_AQI}</h3>
          <p>Category: {data.AQI_Category}</p>
          <div className="pollutants">
            <h4>Pollutants</h4>
            <ul>
              <li>PM2.5: {data['PM2.5_AQI']}</li>
              <li>PM10: {data['PM10_AQI']}</li>
              <li>NO2: {data['NO2_AQI']}</li>
              <li>O3: {data['O3_AQI']}</li>
              <li>CO: {data['CO_AQI']}</li>
              <li>SO2: {data['SO2_AQI']}</li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};
export default CurrentAQI;
