const ForecastAQI = () => {
    const [data, setData] = useState(null);
    const [location, setLocation] = useState(null);

    useEffect(() => {
        if (location) {
            fetch(`http://localhost:5000/api/forecast?lat=${location.lat}&lon=${location.lon}`)
                .then(res => res.json())
                .then(setData)
                .catch(console.error);
        }
    }, [location]);

    const getAQIColor = (aqi) => {
        if (aqi <= 50) return 'good';
        if (aqi <= 100) return 'moderate';
        if (aqi <= 150) return 'sensitive';
        if (aqi <= 200) return 'unhealthy';
        if (aqi <= 300) return 'very-unhealthy';
        return 'hazardous';
    };

    return (
        <div>
            <h2>Forecast AQI</h2>
            <LocationPicker onLocationSelect={setLocation} />
            {data && (
                <>
                    <div className="chart-container">
                        <ResponsiveContainer width="100%" height={400}>
                            <LineChart data={data}>
                                <XAxis dataKey="datetime" />
                                <YAxis />
                                <Tooltip />
                                <Legend />
                                <Line type="monotone" dataKey="Final_AQI" stroke="#8884d8" />
                            </LineChart>
                        </ResponsiveContainer>
                    </div>
                    <div className="forecast-list">
                        {data.map((entry, index) => (
                            <div 
                                key={index} 
                                className={`aqi-display ${getAQIColor(entry.Final_AQI)}`}
                            >
                                <AQIDisplay data={entry} />
                            </div>
                        ))}
                    </div>
                </>
            )}
        </div>
    );
};

export default ForecastAQI;
